import React, { Component } from 'react';
import {connect} from 'react-redux';
import './enroll.css';
import * as actions  from '../../actions/course'
import Sidebar from '../../components/sidebar/sidebar.js';
import queryString from 'query-string';
import { Link } from 'react-router-dom';

class Enroll extends Component {

    constructor(props){
		super(props) ;

		this.state = {
            isStudent: "",
            user_id: "",
            course_id: "",
            all_courses: [],
            enrollment_count: "",
            enrolled: "",
            isEnrollDisabled: "",
            isDropDisabled: "",
            isWaitDisabled: ""
		}
        this.reload = this.reload.bind(this);

	}

    componentDidMount() {
        console.log("Course DID MOUNT")
        this.props.getAllCourses(this.props.user_id);
        if(this.props.isStudent === "on"){
            this.setState({
                isHidden: true
            })
        }
        else{
            this.setState({
                isHidden: false
            })
        }

    }


reload(){
    window.reload();
}


render(){
    console.log(this.props.all_courses)
return (

    <div className="course-content-area ">

      <div className="row course_row">
      <div className="col-xs-3">
        <Sidebar/>
    </div>

          <div className='col-xs- course-option-display'>

              {

                      <div className="assignment-container">
                          <div className="assignment-heading-section">    Courses

                          </div>
                          <table className="table">
                          <thead>
                                <tr>
                                    <th scope="col">#</th>
                                    <th scope="col">Course Term</th>
                                    <th scope="col">Course ID</th>
                                    <th scope="col">Course Name</th>
                                    <th scope="col">Professor</th>
                                    <th scope="col">Enroll</th>
                                    <th scope="col">Drop</th>
                                    <th scope="col">Waitlist</th>
                                </tr>
                        </thead>
                        <tbody>
                          {
                            this.props.all_courses.map((course,index) => {
                                {if(course.enrolled === 'yes'){
                                    this.state.isEnrollDisabled = true;
                                    this.state.isWaitDisabled = true;
                                    this.state.isDropDisabled = false;
                                }}

                                {if(course.enrolled === 'no' && course.enrollment_count < course.student_capacity){
                                    this.state.isEnrollDisabled = false;
                                    this.state.isWaitDisabled = true;
                                    this.state.isDropDisabled = true;
                                }}

                                {if(course.enrolled === 'no' && course.enrollment_count >= course.student_capacity){
                                    this.state.isEnrollDisabled = true;
                                    this.state.isWaitDisabled = false;
                                    this.state.isDropDisabled = true;
                                }}

                                {if(course.enrollment_count == course.student_capacity + course.waitlist_capacity){
                                    this.state.isEnrollDisabled = true;
                                    this.state.isWaitDisabled = true;
                                    this.state.isDropDisabled = true;
                                }}

                                return <tr>


                                <th scope="row">{index}</th>
                                    <td>Spring 2019</td>
                                    <td id={course.course_id}>{course.course_id}</td>
                                    <td>{course.course_name}</td>
                                    <td>{course.user_name}</td>
                                    <td><button className="btn btn-success" hidden={this.state.isEnrollDisabled} onClick= {() =>  reload()}>Enroll</button></td>
                                    <td><button className="btn btn-danger" hidden={this.state.isDropDisabled} onClick={() => {this.props.drop(this.props.user_id,course.course_id); reload()}}>Drop</button></td>
                                    <td><button className="btn btn-warning" hidden={this.state.isWaitDisabled} onClick={() => {this.props.waitlist(this.props.user_id,course.course_id); reload()}}>Waitlist</button></td>

                                </tr>




                              })
                          }
                          </tbody>
                          </table>
                      </div>

              }


          </div>
      </div>
    </div>
);
            }

        }

        function mapStateToProps(state) {
            return {
                user_id: state.auth.user_id,
                all_courses: state.course.all_courses,
                isStudent: state.auth.isStudent,
                enrollment_count: state.course.enrollment_count
            };
        }

        function mapDispatchToProps(dispatch) {
            return {
                getAllCourses : (user_id) => dispatch(actions.getAllCourses(user_id)),
                enroll : (user_id,course_id) => dispatch(actions.enroll(user_id,course_id)),
                drop : (user_id,course_id) => dispatch(actions.drop(user_id,course_id)),
                waitlist : (user_id,course_id) => dispatch(actions.waitlist(user_id,course_id))
            };
        }

export default connect(mapStateToProps,mapDispatchToProps)(Enroll);
