import swal from 'sweetalert';
import React, { Component } from 'react';
import {connect} from 'react-redux';
import { Link } from 'react-router-dom';
import { withRouter } from 'react-router-dom';
import Sidebar from '../sidebar/sidebar';
import './addQuiz.css';
import * as actions  from '../../actions/quiz'

class AddQuiz extends Component{
    constructor(props){
        super(props);
        this.state = {
            questions: [],
            objectToSave: [{question: "", answers: {answer1: "", answer2: "", answer3: "", answer4: ""}}],
            question: "test",
            answers: [],
            counter: 0,
            newQuestion: ""
        }

        this.addChild = this.addChild.bind(this);
        this.updateQuestion = this.updateQuestion.bind(this);
        this.updateAnswer1 = this.updateAnswer1.bind(this);
        this.updateAnswer2 = this.updateAnswer2.bind(this);
        this.updateAnswer3 = this.updateAnswer3.bind(this);
        this.updateAnswer4 = this.updateAnswer4.bind(this);
        this.saveQuiz = this.saveQuiz.bind(this);
    }

addChild(){
    this.setState({
        question: "New"
    })
    this.state.questions.push(this.state.question);
}

removeChild(i){
    this.setState({
        question: "New"
    })
    this.state.questions.pop(i);
}

updateQuestion(e){
    this.setState({
        newQuestion: e.target.value
    })
}

updateAnswer1(e){
    this.state.answers.push(e.target.value)
}

updateAnswer2(e){
    this.state.answers.push(e.target.value)
}

updateAnswer3(e){
    this.state.answers.push(e.target.value)
}

updateAnswer4(e){
    this.state.answers.push(e.target.value)
}

pushQuestionAnswers(){
    this.setState({
        objectToSave: [question]
    })
    if(this.state.newQuestion)
    this.state.questionsToSave.push(this.state.newQuestion)
    this.state.answersToSave.push(this.state.answers)
    this.setState({
        answers: [{}]
    })
}

saveQuiz(){
    this.state.questionsToSave.push(this.state.newQuestion)
}

render() {
    return(
        <div>
            <Sidebar/>
            <div id="assignment_container" className="container">
                <div>
                    <h1 id="h1_assignment"> Add Quiz </h1>
                </div>
                <div className="form-row">
                    <div className="form-group col-md-6 form_assignment">
                        <label htmlFor="quiz_title">Quiz Title</label>
                        <input type="text" className="form-control " name="quiz_title" id="quiz_title" placeholder="Quiz Name"/>
                    </div>
                    <div className="form-group col-md-6 form_assignment">
                        <label htmlFor="due_date">Due Date</label>
                        <input type="date" className="form-control " id="due_date" placeholder="Due Date"/>
                    </div>
                </div>
                <div className = "form-row">
                    <div className="form-group col-lg-12 form_assignment">
                        <label htmlFor="comment">Quiz Instructions</label>
                        <textarea className="form-control " rows="5" name="quiz_instructions" id="comment" placeholder="Quiz Instructions"/>
                    </div>
                </div>

                <div>
                    {
                    this.state.questions.map((question, index) => {
                        return  <div className = "form-row">
                                    <div className="form-group col-lg-12 form_assignment">
                                        <label htmlFor="comment">Question {index+1}</label><i class="far fa-trash-alt" onClick={() => this.removeChild({index})}></i>
                                        <textarea className="form-control quiz_question" rows="1" name="quiz_question" id="comment" placeholder="Question" onChange={this.updateQuestion}/>
                                    </div>
                                    <div className="form-group col-lg-6 form_assignment">
                                        <label htmlFor="comment" className="options">Option 1</label><p className="pCorrect">Correct?</p><input type="checkbox" className="isAnswer"></input>
                                        <textarea className="form-control quiz_answer" rows="1" name="quiz_question" id="comment" placeholder="Option 1" onChange={this.updateAnswers1}/>
                                    </div>
                                    <div className="form-group col-lg-6 form_assignment">
                                        <label htmlFor="comment" className="options">Option 2</label><p className="pCorrect">Correct?</p><input type="checkbox" className="isAnswer"></input>
                                        <textarea className="form-control quiz_answer" rows="1" name="quiz_question" id="comment" placeholder="Option 2" onChange={this.updateAnswers2}/>
                                    </div>
                                    <div className="form-group col-lg-6 form_assignment">
                                        <label htmlFor="comment" className="options">Option 3</label><p className="pCorrect">Correct?</p><input type="checkbox" className="isAnswer"></input>
                                        <textarea className="form-control quiz_answer" rows="1" name="quiz_question" id="comment" placeholder="Option 3" onChange={this.updateAnswers3}/>
                                    </div>
                                    <div className="form-group col-lg-6 form_assignment">
                                        <label htmlFor="comment" className="options">Option 4</label><p className="pCorrect" >Correct?</p><input type="checkbox" className="isAnswer"></input>
                                        <textarea className="form-control quiz_answer" rows="1" name="quiz_question" id="comment" placeholder="Option 4" onChange={this.updateAnswers4}/><br/>
                                    </div>
                                </div>
                    })
                    }
                </div>
                <button className="btn btn-primary" onClick={() => {this.addChild(); this.pushQuestionAnswers()}}>Add Question</button><br/><br/>
                <button className="btn btn-primary" onClick={() => {this.props.saveQuiz(this.state.questionsToSave, this.state.answersToSave); this.saveQuiz()}}>Save Quiz</button>


            </div>
        </div>

    );
}
}

function mapStateToProps(state){
    return{

    };
}

function mapDispatchToProps(dispatch){
    return{
        saveQuiz: (questions, answers) => dispatch(actions.saveQuiz(questions, answers))
    };
}

export default (connect(mapStateToProps,mapDispatchToProps)(AddQuiz))
