import React, { Component } from 'react';
import {connect} from 'react-redux';
import Sidebar from '../sidebar/sidebar'
import './message.css'

class Message extends Component {
    constructor(props) {
        super(props);
        const DUMMY_DATA = [
            {
              user_name: "perborgen",

            },
            {
              user_name: "janedoe",
            }
          ]

        this.state = {
            messages: DUMMY_DATA
        }

    }


    render() {

        return (
            <div>
            <div class="col-sm-3 col-md-3 col-lg-3">
            <Sidebar/>
            </div>
            <div class="col-sm-9 col-md-9 col-lg-9">
                <div className = "container message_container">
                    <div className="col-sm-3 col-md-3 col-lg-3" id="user_list">
                        <p id="message_list">Messages</p>
                        <ul className="message-list">                 
                        {this.props.messages.map(message => {
                        return (
                        <li key={message.id}>
                            <div>
                            {message.senderId}
                            </div>
                            <div>
                            {message.text}
                            </div>
                        </li>
                        )
                    })}
                    </ul>
                    </div>
                </div>
            </div>
            </div>
        );

    }

}

