import initial from './initialState';

const reducer = (state = initial, action) => {

    switch (action.type) {
        case "LOGIN_STATUS" : {
			console.log("userID Payload " + action.payload.data.user_id)
            if(action.payload.status===200)
			{
				console.log("reducer fired")
				console.log(action.payload.data.user_id)
				return {...state,isLogged:true,user_id:action.payload.data.user_id};
			}
			else
			{
				console.log('REDUCER ISVALID ' + action.payload);
				return {...state,isLogged:false};	
			}
			// return {
			// 	...state,
			// 	user_id : action.payload
			// }	

		}

		case "SIGNUP_STATUS" : {
            if(action.payload===true)
			{
                console.log("signup fired")
				return {...state,isSigned:true};
			}
			else
			{
				console.log('REDUCER ISVALID ' + action.payload);
				return {...state,isSigned:false};	
			}	

        }

        default :
            return state;
    }
};

export default reducer;
