import React, { Component } from 'react';
import {connect} from 'react-redux';
import './courseContent.css';
import * as actions  from '../../../actions/course'
import Sidebar from '../../../components/sidebar/sidebar.js';
import queryString from 'query-string';
import { Link } from 'react-router-dom';



class CourseContent extends Component {

	constructor(props){
		super(props) ;

		this.state = {
		    courseOptions : ["Home","Announcement","Assignment","People","Grades"],
            selectedTab : "Home",
            assignment : [
                {
                    name : "Assignment" ,
                    due : "5 July",
                    score : "6",
                    score_from : "10"
                },
                {
                    name : "Assignment" ,
                    due : "5 July",
                    score : "6",
                    score_from : "10"
                },
                {
                    name : "Assignment" ,
                    due : "5 July",
                    score : "6",
                    score_from : "10"
                },
                {
                    name : "Assignment" ,
                    due : "5 July",
                    score : "6",
                    score_from : "10"
                }]

               ,
            peoples : [{
                    name : "Palash Hedau",
                    role : "Student",
                    image : ""
                },
                {
                    name : "Palash Hedau",
                    role : "Student",
                    image : ""
                },
                {
                    name : "Palash Hedau",
                    role : "Student",
                    image : ""
                }],
            announcement : [
                {
                    user : "Palash",
                    image : "",
                    heading : "New Registrant for Fall 2019 student",
                    content : "My name is something anf I am new studemt and want to do something with respect to course",
                    date : "6 Dec 2018 at 14:58"
                },
                {
                    user : "Palash",
                    image : "",
                    heading : "New Registrant for Fall 2019 student",
                    content : "My name is something anf I am new studemt and want to do something with respect to course",
                    date : "6 Dec 2018 at 14:58"
                },
                {
                    user : "Palash",
                    image : "",
                    heading : "New Registrant for Fall 2019 student",
                    content : "My name is something anf I am new studemt and want to do something with respect to course",
                    date : "6 Dec 2018 at 14:58"
                }
            ],
            isStudent: "",
            isHidden: ""
		}
        this.selectTab = this.selectTab.bind(this);
        this.routeChange = this.routeChange.bind(this);
	}

    componentDidMount() {
        if(this.props.isStudent === "on"){
            this.setState({
                isHidden: true
            })
        }
        else{
            this.setState({
                isHidden: false
            })
        }
        console.log("Course DID MOUNT")
        let obj = queryString.parse(this.props.location.search)
        this.props.getCourseDetails(obj.course);
    }

    componentWillReceiveProps(newProps) {

	}

	selectTab(val){
	    console.log(val)
    }

    routeChange() {
        let path = "/addAssignment";
        this.props.history.push(path);
    }


    render() {

        return (

                <div className="course-content-area ">

                  <div className="row course_row">
                  <div className="col-xs-3">
                    <Sidebar/>
                </div>
                      <div className="col-xs-3 course-option-area">
                          {
                              this.state.courseOptions.map((option,index) => {
                                  return <div  onClick={() => {
                                    this.setState({selectedTab : option})
                                  }}  className="row single-course-option" key={index}>
                                      <span>{option}</span>
                                  </div>
                              })
                          }
                      </div>
                      <div className='col-xs-9 course-option-display'>
                          {
                              this.state.selectedTab === "Home" ?
                                  <div>
                                      { this.state.selectedTab }
                                      AMAN
                                  </div>
                                  :
                                  <span></span>
                          }

                          {
                              this.state.selectedTab === "Announcement" ?
                                  <div className="announcement">

                                      {
                                          this.props.courseDetails.announcement.map((announce, index) => {
                                              return <div key={index} className="row announcement-row">
                                                          <div className="col-xs-2 announcement-image">
                                                              <img className="people-image" src="/assets/images/user_image.png"/>
                                                          </div>
                                                          <div className="col-xs-10 announcement-content">
                                                              <h4><b>{announce.heading}</b></h4>
                                                              <br/>
                                                              <span className="pull-right assignment-due">{announce.date}</span>
                                                              <br/>
                                                              <span>{announce.content.substring(0,80)}...</span>

                                                          </div>
                                                      </div>
                                          })
                                      }

                                  </div>
                                  :
                                  <span></span>
                          }

                          {
                              this.state.selectedTab === "Assignment" ?
                                  <div className="assignment-container">
                                      <div className="assignment-heading-section">    Assignment
                                            <Link to="/addAssignment" id="assignment_add" className="btn btn-primary" >Add Assignment</Link>
                                      </div>


                                      {
                                          this.props.courseDetails.allAssignment.map((assign, index) => {
                                              return <div className="single-assignment-area">
                                                  {assign.name} {index}
                                                  <br/>
                                                  <span className="assignment-due">{assign.due}</span>
                                              </div>
                                          })
                                      }

                                  </div>
                                  :
                                  <span></span>
                          }

                          {
                              this.state.selectedTab === "People" ?

                                  <div className="assignment-container">
                                      <div className="assignment-heading-section"> People </div>
                                        <table className="table table-striped">
                                          <thead>
                                              <tr>
                                                  <th scope="col">#</th>
                                                  <th scope="col"></th>
                                                  <th scope="col">Name</th>
                                                  <th scope="col">Role</th>
                                              </tr>
                                          </thead>
                                          <tbody>

                                          {
                                              this.props.courseDetails.peoples.map((people,index) => {
                                                  return <tr>
                                                          <th scope="row">{index + 1 }</th>
                                                          <td><img alt=""  className="people-image" src="/assets/images/user_image.png"/></td>
                                                          <td>{people.name}</td>
                                                          <td>{people.role}</td>
                                                      </tr>
                                              })
                                          }



                                          </tbody>
                                      </table>

                                  </div>


                                  :
                                  <span></span>
                          }

                          {
                              this.state.selectedTab === "Grades" ?
                                  <div className="assignment-container">
                                      <div className="assignment-heading-section">  Grades</div>

                                      <table className="table">
                                          <thead className="thead-dark">
                                          <tr>
                                              <th scope="col">#</th>
                                              <th scope="col">Name</th>
                                              <th scope="col">Due</th>
                                              <th scope="col">Score</th>
                                              <th scope="col">Out of</th>
                                          </tr>
                                          </thead>


                                          <tbody>

                                          {
                                              this.props.courseDetails.assignment.map((assign, index) => {
                                                  return <tr>
                                                      <th scope="row">{index + 1 }</th>
                                                      <td>{ assign.name}</td>
                                                      <td>{assign.due}</td>
                                                      <td>{assign.score}</td>
                                                      <td>{assign.score_from}</td>
                                                  </tr>
                                              })
                                          }

                                          </tbody>
                                      </table>





                                  </div>
                                  :
                                  <span></span>
                          }
                      </div>
                  </div>
                </div>
        );
    }
}


function mapStateToProps(state) {

    return {
       course_list: state.dashboard.course_list,
        courseDetails : state.course.courseDetails,
        isStudent: state.auth.isStudent
    };
}

function mapDispatchToProps(dispatch) {

    return {
        getCourseDetails : (course_id) => dispatch(actions.getCourseDetails(course_id))
    };
}

export default connect(mapStateToProps,mapDispatchToProps)(CourseContent);
