import React, { Component } from 'react';
import {connect} from 'react-redux';
import { withRouter } from 'react-router-dom';
import './signin.css';
import * as actions from '../../actions/auth';
import { Link } from 'react-router-dom';
import Home from '../home/home';

class Signin extends Component {


	componentWillReceiveProps(newProps) {
		console.log('Will Receive Props ' , newProps)
		  if(newProps.isLogged){
			console.log('Pushing to the page ')
			newProps.history.push('/home');
		  }
	}



	constructor(props){
		super(props) ;

		this.state = {
			user_id : '' , 
			password : ''

		}
	}

	onChangeUserid(e){
		this.setState({
			user_id : e.target.value
		})
	}
	onChangePassword(e){
		this.setState({
			password : e.target.value
		})
	}

    render() {
		return (
	        <div>	
	        	<nav>
	           		Connecting to <img id = "sjsu_logo" src="/assets/images/sjsu_logo.png"/>
	        	</nav>
		        <div className="container">
		                <div id="logo_div">
		                    <img id="sjsu_full_logo" src="/assets/images/sjsu_full_logo.png"/>
		                </div>
		                <div id="form_div">
		                    <p><strong>Sign In</strong></p>
		                    <form id="inner_form">
		                        <input id="input_form" name="user_id" type="number" placeholder="ID Number" onChange={this.onChangeUserid.bind(this)}/><br/>
		                        <input id="input_form" name="password" type="password" placeholder="Password" onChange={this.onChangePassword.bind(this)}/><br/>
		                        <input id="input_form" type="button" className="btn btn-primary" value="Sign In" onClick={() =>this.props.login(this.state.user_id, this.state.password)}/>
		                    </form>
		                    <a id="signup_link" href="http://localhost:3000/signup">Don't have an account? Sign Up</a>
						</div>
		        </div>

      		</div>
        );
    }
}


function mapStateToProps(state) {
    	return { 
			isLogged: state.auth.isLogged,
		};

}

function mapDispatchToProps(dispatch) {
    return {
    	login : (user_id,password) => dispatch(actions.login(user_id,password))
    };
}


export default withRouter(connect(mapStateToProps , mapDispatchToProps )(props => <Signin {...props}/>));
