import swal from 'sweetalert';
import React, { Component } from 'react';
import {connect} from 'react-redux';
import { Link } from 'react-router-dom';
import { withRouter } from 'react-router-dom';
import Sidebar from '../sidebar/sidebar';

class AddQuiz extends Component{
    constructor(props){
        super(props);
        this.state = {
            questions: [],
            answers: []
        }
    }


render() {
    return(
        <div>
            <Sidebar/>
            <div id="assignment_container" className="container">
                <div>
                    <h1 id="h1_assignment"> Add Quiz </h1>
                </div>
                <div className="form-row">
                    <div className="form-group col-md-6 form_assignment">
                        <label htmlFor="course_id">Quiz Title</label>
                        <input type="text" className="form-control " name="course_id" id="course_id" placeholder="Quiz Name"/>
                    </div>
                    <div className="form-group col-md-6 form_assignment">
                        <label htmlFor="due_date">Due Date</label>
                        <input type="date" className="form-control " value={this.state.assignment_date} id="due_date" placeholder="Due Date" onChange={this.dateChange}/>
                    </div>
                </div>
                <div className = "form-row">
                    <div className="form-group col-lg-12 form_assignment">
                        <label htmlFor="course_id">Quiz Instructions</label>
                        <input type="text" className="form-control " name="course_id" id="course_id" placeholder="Quiz Instructions"/>
                    </div>
                </div>
                <button className="btn btn-primary">Add Question</button>
            </div>
        </div>

    );
}
}

