import React, { Component } from 'react';
import {connect} from 'react-redux';
import Sidebar from '../sidebar/sidebar'
import './message.css'

class Message extends Component {
    constructor(props) {
        super(props);
        const DUMMY_DATA = [
            {
              from: "aman",
              to: "drashti",
              message: "Hi Honey!Hi Honey!Hi Honey!Hi Honey!Hi Honey!Hi Honey!Hi Honey!Hi Honey!Hi Honey!Hi Honey!Hi Honey!Hi Honey!Hi Honey!Hi Honey!Hi Honey!Hi Honey!Hi Honey!Hi Honey!Hi Honey!",
              time : "8:00 Am"

            },
            {
                from: "drashti",
                to: "aman",
                message: "Hi Honey!",
                time : "8:00 Am"
            }
          ]

        this.state = {
            messages: DUMMY_DATA
        }

    }

    componentDidMount() {
        console.log("Course DID MOUNT")
        this.props.getAllMessages(this.props.user_id);
    }

    componentWillReceiveProps(newProps) {    
        console.log('Component WILL RECIEVE PROPS!')
        console.log(newProps)
        this.setState({
            display_messages: newProps.all_messages
        })
    }

    render() {

        return (
            <div>
            <div class="col-sm-3 col-md-3 col-lg-3">
            <Sidebar/>
            </div>
            <div className="col-sm-9 col-md-9 col-lg-9">
                <div>
                    <button className="btn btn-primary" id="compose_button">Compose</button>
                </div>
                <div className = "container message_container">
                    {this.state.messages.map(message => {
                    return(
                    <div className="message_details row">
                        <div className="col-sm-3 col-md-3 col-lg-3">
                            From: {message.from} <br/>
                            To: {message.to}
                        </div>
                        <div className="col-sm-6 col-md-6 col-lg-6">
                            Message: {message.message}
                        </div>
                        <div className="col-sm-3 col-md-3 col-lg-3">
                            Time: {message.time}
                        </div>

                    </div>
                    )})}
                </div>
            </div>
            </div>
        );

    }

}

function mapStateToProps(state) {

    return {
        user_id: state.auth.user_id,
        all_messages: state.message.all_courses,
    };
}

function mapDispatchToProps(dispatch) {

    return {

       };
}
export default connect(mapStateToProps,mapDispatchToProps)(Message);
