import React, { Component } from 'react';
import { Route, Switch } from 'react-router-dom';
import {connect} from 'react-redux';
import './App.css';
import {withRouter} from 'react-router-dom';
import Signin from './components/auth/signin.js';
import Signup from './components/auth/signup.js';
import Dashboard from './components/dashboard/dashboard.js';
import Profile from './components/profile/profile.js';
import Sidebar from './components/sidebar/sidebar.js';
import CourseContent  from './components/dashboard/courseContent/courseContent';
import Assignment from './components/assignment/assignment.js';
import AddAssignment from './components/assignment/addAssignment.js';
import AddCourse from './components/course/addCourse.js';


import {BrowserRouter} from "react-router-dom";

class App extends Component {

    componentDidMount(){
    }

    componentDidUpdate(prevProps) {
        this.props.checkSession();
    }



    render() {
        const isLogged = this.props.isLogged;
        return (
            <div >

isLogged === undefined ? (
          <div className="text-center"><h1>Loading...</h1></div>
        ) : (
          <BrowserRouter>
            <Switch>
              <Route exact path='/' render={() => (
                isLogged ? (
                  <Redirect to="/dashboard/d"/>
                ) : (
                  <Signin/>
                )
              )}/>

                <Route path='/dashboard/d' render={() => (
                  !isLogged ? (
                    <Redirect to="/"/>
                  ) : (
                    <Dashboard/>
                  )
                )}/>
                <Route path='/profile' render={() => (
                  !isLogged ? (
                    <Redirect to="/"/>
                  ) : (
                    <Profile/>
                  )
                )}/>
                <Route path='/assignment' render={() => (
                  !isLogged ? (
                    <Redirect to="/"/>
                  ) : (
                    <Assignment/>
                      )
                )}/>
                <Route path='/addAssignment' render={() => (
                  !isLogged ? (
                    <Redirect to="/"/>
                  ) : (
                    <AddAssignment/>
                  )
                )}/>
                <Route path='/addCourse' render={() => (
                    !isLogged ? (
                      <Redirect to="/"/>
                    ) : (
                      <Console/>
                    )
                  )}/>
                  <Route path='/host' render={() => (
                      !isLogged ? (
                        <Redirect to="/"/>
                      ) : (
                        <Host/>
                      )
                    )}/>

            </Switch>
          </BrowserRouter>
)

                {/* <div className="col-xs-9"> */}
                <Switch>
                    <Route exact path='/' component={Signin}/>
                    <Route exact path='/signin' component={Signin}/>
                    <Route path='/dashboard' component={Dashboard}/>
                    <Route exact path='/signup' component={Signup}/>
                    <Route exact path='/profile' component={Profile}/>
                    <Route exact path='/courses/:id' component={CourseContent}/>
                    <Route exact path='/assignment' component={Assignment}/>
                    <Route exact path='/addassignment' component={AddAssignment}/>
                    <Route exact path='/addCourse' component={AddCourse}/>

                </Switch>
                {/* </div> */}
            </div>



        )
    }
}

function mapStateToProps(state) {
    return {isLogged:state.auth.isLogged};
}

function mapDispatchToProps(dispatch) {
    return {

    };
}

export default withRouter(connect(mapStateToProps , mapDispatchToProps )(props => <App {...props}/>));
