import React, { Component } from 'react';
import {connect} from 'react-redux';
import './enroll.css';
import * as actions  from '../../actions/course'
import Sidebar from '../../components/sidebar/sidebar.js';
import queryString from 'query-string';
import { Link } from 'react-router-dom';

class Enroll extends Component {

    constructor(props){
		super(props) ;

		this.state = {
            isStudent: "",
            user_id: "",
            course_id: "",
            all_courses: []
		}


	}

    componentDidMount() {
        console.log("Course DID MOUNT")
        this.props.getAllCourses();


    }


// enroll(course_id){

//     console.log()
//     // this.props.all_courses.map((course,index) => {
//     // this.props.enroll(this.props.user_id, course.course_id);
//     // })


// }

render(){
    console.log(this.props.all_courses)
return (

    <div className="course-content-area ">

      <div className="row course_row">
      <div className="col-xs-3">
        <Sidebar/>
    </div>

          <div className='col-xs- course-option-display'>

              {

                      <div className="assignment-container">
                          <div className="assignment-heading-section">    Courses

                          </div>
                          <table className="table">
                          <thead>
                                <tr>
                                    <th scope="col">#</th>
                                    <th scope="col">Course Term</th>
                                    <th scope="col">Course ID</th>
                                    <th scope="col">Course Name</th>
                                    <th scope="col">Professor</th>
                                    <th scope="col">Enroll</th>
                                </tr>
                        </thead>
                          {
                            this.props.all_courses.map((course,index) => {
                                return <div key={index} className="">
                            <tbody>
                                <tr>
                                <th scope="row">{index}</th>
                                    <td>Spring 2019</td>
                                    <td id={course.course_id}>{course.course_id}</td>
                                    <td>{course.course_name}</td>
                                    <td>{course.user_id}</td>
                                    <td><button className="btn btn-primary" onClick={() => this.props.enroll(this.props.user_id,course.course_id)}>Enroll</button></td>
                                    <td><button className="btn btn-primary" onClick={() => this.props.enroll(this.props.user_id,course.course_id)}>Drop</button></td>
                                </tr>
                                </tbody>
                                  </div>


                              })
                          }
                          </table>
                      </div>

              }


          </div>
      </div>
    </div>
);
            }

        }

        function mapStateToProps(state) {
            return {
                user_id: state.auth.user_id,
                all_courses: state.course.all_courses,
                isStudent: state.auth.isStudent
            };
        }

        function mapDispatchToProps(dispatch) {
            return {
                getAllCourses : () => dispatch(actions.getAllCourses()),
                enroll : (user_id,course_id) => dispatch(actions.enroll(user_id,course_id))
            };
        }

export default connect(mapStateToProps,mapDispatchToProps)(Enroll);
