		// 	console.log('Pushing to the page ')
		// 	newProps.history.push('/home');
		//   }
	}



    render() {
        return (

            	<div>
                <Sidebar/>
                      <div id="assignment_container" className="container">
        <div>
            <h1 id="h1_assignment"> Add Assignment </h1>
        </div>
        <form>
                <div className="form-row">
                  <div className="form-group col-md-6 form_assignment">
                    <label htmlFor="assignment_id">Assignment ID</label>
                    <input type="number" className="form-control " id="assignment_id" placeholder="Assignment ID"/>
                  </div>
                  <div className="form-group col-md-6 form_assignment">
                        <label htmlFor="assignment_name">Assignment Name</label>
                        <input type="text" className="form-control " id="assignment_name" placeholder="Assignment Name"/>
                  </div>
                </div>
                <div className="form-row">
                    <div className="form-group col-md-6 form_assignment">
                        <label htmlFor="course_department">Course Department</label>
                        <input type="text" className="form-control " id="course_department" placeholder="Course Department"/>
                    </div>
                    <div className="form-group col-md-6 form_assignment">
                        <label htmlFor="due_date">Due Date</label>
                        <input type="date" className="form-control " id="due_date" placeholder="Due Date"/>
                    </div>
                </div>
                <div className="form-row">
                <div className="form-group col-md-6 form_assignment">
                            <label htmlFor="assignment_description">Assignment Description</label>
                            <input type="text" className="form-control " id="assignment_description"/>
                    </div>
                </div>
                <div className="form-row">
                    <button type="submit" className="btn btn-success" id="save_">Save</button>
                </div>
              </form>
            </div>  

      			</div>
        );
    }
}


function mapStateToProps(state) {

    return {
        user_id: state.profile.user_id,


    };
}

function mapDispatchToProps(dispatch) {

    return {
        //getProfile : (user_id) => dispatch(actions.getProfile(user_id))
    };
}

export default connect(mapStateToProps,mapDispatchToProps)(AddAssignment);
