import swal from 'sweetalert';
import React, { Component } from 'react';
import {connect} from 'react-redux';
import { Link } from 'react-router-dom';
import { withRouter } from 'react-router-dom';
import Sidebar from '../sidebar/sidebar';
import './takeQuiz.css';
import * as actions  from '../../actions/quiz';
import queryString from 'query-string';

class TakeQuiz extends Component{
    constructor(props){
        super(props);
        this.state = {
            quiz_id: ""
        }


    }

componentDidMount() {
    console.log("Course DID MOUNT")
    let obj = queryString.parse(this.props.location.search)
    this.props.getQuizDetails(obj.quiz);
    sessionStorage.setItem("quiz_id", obj.quiz);
    this.setState({
        quiz_id: obj.quiz,
        quiz_details: []
    })
}

selectAnswer(){

}

reload_screen() {
    setTimeout(function() {
        window.location.reload();
    }, 1000);

}

pushQuestionAnswers(){
    if(this.state.newQuestion != ""){
        let objectToSave =  [{
                                question: this.state.newQuestion, 
                                answers: 
                                {
                                    answer1: this.state.answer1, 
                                    answer2: this.state.answer2, 
                                    answer3: this.state.answer3, 
                                    answer4: this.state.answer4
                                },
                                isRight: this.state.isRight
                            }]

        this.state.finalObject.push(objectToSave)
    }
}


render() {
    return(
        <div>
            <Sidebar/>
            <div id="assignment_container" className="container">
                <div>
                    <h1 id="h1_assignment"> Take Quiz </h1>
                </div>
                <div className="form-row">
                    <div className="form-group col-md-6 form_assignment">
                        <label htmlFor="quiz_title">Quiz</label>
                        <p name="quiz_title" readOnly/>
                    </div>
                </div>
                <div>
                    {/* { */}
                    {/* this.state.quiz_details.map((question, index) => { */}
                        return  <div className = "form-row">
                                    <div className="form-group col-lg-12 form_assignment">
                                        <label htmlFor="comment">Question 1</label><i class="far fa-trash-alt"></i>
                                        <p name="quiz_question" id="comment">{question.question}</p>
                                    </div>
                                    <div className="form-group col-lg-6 form_assignment">
                                        <label htmlFor="comment" className="options">Option 1</label><input type="radio" className="isAnswer" name = "answer" onChange={() => this.setState({isRight: "ans1"})}></input>
                                        <p name="quiz_question" id="comment">{question.ans1}</p>
                                    </div>
                                    <div className="form-group col-lg-6 form_assignment">
                                        <label htmlFor="comment" className="options">Option 2</label><input type="radio" className="isAnswer" name = "answer" onChange={() => this.setState({isRight: "ans2"})}></input>
                                        <p name="quiz_question" id="comment">ans</p>
                                    </div>
                                    <div className="form-group col-lg-6 form_assignment">
                                        <label htmlFor="comment" className="options">Option 3</label><input type="radio" className="isAnswer" name = "answer" onChange={() => this.setState({isRight: "ans3"})}></input>
                                        <p name="quiz_question" id="comment">ans2</p>
                                    </div>
                                    <div className="form-group col-lg-6 form_assignment">
                                        <label htmlFor="comment" className="options">Option 4</label><input type="radio" className="isAnswer" name = "answer" onChange={() => this.setState({isRight: "ans4"})}></input>
                                        <p name="quiz_question" id="comment">ans1</p>
                                    </div>
                                </div>
                    {/* }) */}
                    {/* } */}
                </div>
                <button className="btn btn-primary" onClick={() => {this.pushQuestionAnswers(); this.props.saveQuiz(this.state.finalObject, this.state.quizId, this.state.quizName, this.state.quizInstructions, this.state.quizDate, this.state.course_id); this.reloadScreen()}}>Submit Quiz</button>


            </div>
        </div>

    );
}
}

function mapStateToProps(state){
    return{
       quiz_details: state.quiz.quiz_details
    };
}

function mapDispatchToProps(dispatch){
    return{
        getQuizDetails: (quiz_id) => dispatch(actions.getQuizDetails(quiz_id)),
        // saveQuiz: (finalObject, id, name, instructions, date, course_id) => dispatch(actions.saveQuiz(finalObject, id, name, instructions, date, course_id))
    };
}

export default withRouter(connect(mapStateToProps,mapDispatchToProps)(TakeQuiz))
