import React, { Component } from 'react';
import {connect} from 'react-redux';
import './profile.css';
import * as actions from '../../actions/profile';
import Sidebar from '../sidebar/sidebar'


class Profile extends Component {

	// constructor(props){
	// 	super(props) ;

	// 	this.state = {
    //         sidebar_width1 : "0px",
    //         sidebar_width2 : "0px",
    //         sidebar_width3 : "0px"	
    //     }
    //     this.onClickSidebar1 = this.onClickSidebar1.bind(this);
    //     this.onClickSidebar2 = this.onClickSidebar2.bind(this);
    //     this.onClickSidebar3 = this.onClickSidebar3.bind(this);
    // }

    constructor(props){
		super(props) ;

		this.state = {
            isDisabled: true,
            user_id: "125",
            email: "",
            phone_number: "",
            about_me: "",
            city: "",
            country: "",
            company: "",
            school: "",
            hometown: "",
            languages: "",
            gender: ""
        }

        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangePhoneNumber = this.onChangePhoneNumber.bind(this);
        this.onChangeAboutMe = this.onChangeAboutMe.bind(this);
        this.onChangeCity = this.onChangeCity.bind(this);
        this.onChangeCountry = this.onChangeCountry.bind(this);
        this.onChangeCompany = this.onChangeCompany.bind(this);
        this.onChangeSchool = this.onChangeSchool.bind(this);
        this.onChangeLanguage = this.onChangeLanguage.bind(this);
        this.onChangeGender = this.onChangeGender.bind(this);
        this.onChangeHometown = this.onChangeHometown.bind(this);
	}


     componentDidMount() {
        console.log('Component DID MOUNT!');
        this.props.getProfile(this.state.user_id);

     }

     componentWillReceiveProps(newProps) {
        console.log('Will Receive Props ' , newProps)

        this.setState({
            user_id: newProps.user_id,
            email: newProps.email,
            name: newProps.name,
            phone_number: newProps.phone_number,
            about_me: newProps.about_me,
            city: newProps.city,
            company: newProps.company,
            school: newProps.school,
            country: newProps.country,
            hometown: newProps.hometown,
            languages: newProps.languages,
            gender: newProps.gender
		})
		//   if(newProps.isLogged){
		// 	console.log('Pushing to the page ')
		// 	newProps.history.push('/home');
		//   }
	}

    editProfile(e){
        this.setState({
            isDisabled: e.target.value
        })
    }

    onChangeEmail(e){
        this.setState({
            email: e.target.value
        })

    }

    onChangePhoneNumber(e){
        this.setState({
            phone_number: e.target.value
        })

    }

    onChangeAboutMe(e){
        this.setState({
            about_me: e.target.value
        })
    }

    onChangeCity(e){
        this.setState({
            city: e.target.value
        })
    }

    onChangeCountry(e){
        this.setState({
            country: e.target.value
        })
    }

    onChangeCompany(e){
        this.setState({
            company: e.target.value
        })
    }

    onChangeSchool(e){
        this.setState({
            school: e.target.value
        })
    }

    onChangeLanguage(e){
        this.setState({
            languages: e.target.value
        })
    }

    onChangeGender(e){
        this.setState({
            gender: e.target.value
        })
    }

    onChangeHometown(e){
        this.setState({
            hometown: e.target.value
        })
        console.log(e.target.value);
    }

    render() {
        return (
            	<div>
                        <Sidebar/>
                        <div className="container">
                            <div>
                                <a><img id="user_image" src="/assets/images/user_image.png"/></a>
                                <p className="profile_p">Drusti Thakkar</p>
                                <button id="edit_button" onClick={this.editProfile.bind(this)} ><i className="fas fa-edit"></i></button>
                            </div>
                            <form className="edit_form">
                                <input className="input_form" type="text" placeholder="Name" name="name" value={this.state.name} disabled="true" />
                                <input id="sjsuid" type="number" placeholder="SJSU ID" name="user_id" value={this.state.user_id} disabled="true"/>
                                <input className="input_form" type="email" name="email" placeholder="Email" value={this.state.email} disabled= {this.state.isDisabled} onChange={this.onChangeEmail}/>
                                <input className="input_form" type="number" name="phone_number" placeholder="Contact" value={this.state.phone_number} disabled={this.state.isDisabled} onChange={this.onChangePhoneNumber}/>
                                <input className="input_form" type="text" name="about_me" placeholder="About Me" value={this.state.about_me} disabled={this.state.isDisabled} onChange={this.onChangeAboutMe}/>
                                <input className="input_form" type="text" name="city" placeholder="City" value={this.state.city} disabled={this.state.isDisabled} onChange={this.onChangeCity}/>
                                <input className="input_form" type="text" name="country" placeholder="Country" value={this.state.country} disabled={this.state.isDisabled} onChange={this.onChangeCountry}/>
                                <input className="input_form" type="text" name="company" placeholder="Company" value={this.state.company} disabled={this.state.isDisabled} onChange={this.onChangeCompany}/>
                                <input className="input_form" type="text" name="school" placeholder="School" value={this.state.school} disabled={this.state.isDisabled} onChange={this.onChangeSchool}/>
                                <input className="input_form" type="text" name="gender" placeholder="Gender" value={this.state.gender} disabled={this.state.isDisabled} onChange={this.onChangeGender}/>
                                <input className="input_form" type="text" name="languages" placeholder="Languages" value={this.state.languages} disabled={this.state.isDisabled} onChange={this.onChangeLanguage}/>
                                <input className="input_form" type="text" name="hometown" placeholder="Hometown" value={this.state.hometown} disabled={this.state.isDisabled} onChange={this.onChangeHometown}/><br/>
                                <input className="input_form" type="button" value="Save" disabled={this.state.isDisabled} onClick={() =>this.props.setProfile(this.state.user_id, this.state.email, this.state.phone_number, this.state.about_me, this.state.city, this.state.company, this.state.school, this.state.country, this.state.hometown, this.state.languages, this.state.gender)}/>
                            </form>
                         </div>
      			</div>
        );
    }
}


function mapStateToProps(state) {

    return {
        user_id: state.profile.user_id,
        name: state.profile.name,
        email: state.profile.email,
        phone_number: state.profile.phone_number,
        about_me: state.profile.about_me,
        city: state.profile.city,
        company: state.profile.company,
        school: state.profile.school,
        country: state.profile.country,
        hometown: state.profile.hometown,
        languages: state.profile.languages,
        gender: state.profile.gender 

    };
}

function mapDispatchToProps(dispatch) {

    return {
        getProfile : (user_id) => dispatch(actions.getProfile(user_id)),
        setProfile : (user_id, email, phone_number, about_me, city, company, school, country, hometown, languages, gender) => dispatch(actions.setProfile(user_id, email, phone_number, about_me, city, company, school, country, hometown, languages, gender))
    };
}

export default connect(mapStateToProps,mapDispatchToProps)(Profile);
