import React, { Component } from 'react';
import {connect} from 'react-redux';
import Sidebar from '../sidebar/sidebar'
import './message.css'
import * as actions  from '../../actions/message'

class Message extends Component {
    constructor(props) {
        super(props);
        this.state = {
            messages: []
        }

    }

    componentDidMount() {
        console.log("Course DID MOUNT")
        this.props.getAllMessages(this.props.user_id);
    }

    componentWillReceiveProps(newProps) {    
        console.log('Component WILL RECIEVE PROPS!')
        console.log(newProps)
        this.setState({
            messages: newProps.all_messages
        })
    }

    showModal = () => {
        this.setState({ show: true });
      };
    render() {

        return (
            <div>
            <div class="col-sm-3 col-md-3 col-lg-3">
            <Sidebar/>
            </div>
            <div className="col-sm-9 col-md-9 col-lg-9">
                <div>
                    <button className="btn btn-primary" id="compose_button">Compose</button>
                </div>
                <div className = "container message_container">
                    {this.state.messages.map(message => {
                    return(
                    <div className="message_details row">
                        <div className="col-sm-3 col-md-3 col-lg-3">
                            From: {message.from} <br/>
                            To: {message.to}
                        </div>
                        <div className="col-sm-6 col-md-6 col-lg-6">
                            Message: {message.message}
                        </div>
                        <div className="col-sm-3 col-md-3 col-lg-3">
                            Time: {message.time}
                        </div>

                    </div>
                    )})}
                </div>
            </div>
            </div>
        );

    }

}

function mapStateToProps(state) {

    return {
        user_id: state.auth.user_id,
        all_messages: state.message.all_messages,
    };
}

function mapDispatchToProps(dispatch) {

    return {
        getAllMessages : (user_id) => dispatch(actions.getAllMessages(user_id)),
       };
}
export default connect(mapStateToProps,mapDispatchToProps)(Message);
