import axios from "axios";
import fileDownload from 'js-file-download';
const FormData = require('form-data');

export function addNewAnnouncement(state) {


  return function(dispatch){
    axios.post('http://localhost:8000/api/createAannouncement?announcement_id='+state.announcement_id+'&assignment_name='+state.announcement_name+
        '&assignment_description='+state.announcement_description+'&course_id='+sessionStorage.getItem("course_id"))
        .then(function (response) {
            console.log(response)
            if(response.status===200) {
                dispatch({ type: "CREATE_ANNOUNCEMENT_SUCCESS", payload: {} })
            }
        })
        .catch(function (error) {
            dispatch({type: "CREATE_ANNOUNCEMENT_FAILURE",payload: false})
        });
  }
}
